#1 Quicksort
def quicksort(arr):
    if len(arr) <= 1:
        return arr
    else:
        pivot = random.choice(arr)
        less = [x for x in arr if x < pivot]
        equal = [x for x in arr if x == pivot]
        greater = [x for x in arr if x > pivot]
        return quicksort(less) + equal + quicksort(greater)

# Генерируем случайный список целых чисел от 0 до 999999
arr = random.sample(range(1000000), 1000000)

# Сортируем список с помощью Quicksort
sorted_arr = quicksort(arr)

# Печатаем отсортированный список
print('Quicksort:',sorted_arr)



#2 Shaker sort

# создаем список 99 999 случайных вещ чисел в диапазоне [-1, 1]
list_float_numbers = [random.uniform(-1, 1) for numbers in range(999) ]

left_border = 0 
right_border = len(list_float_numbers) - 1

while left_border <= right_border:
    for i in range(left_border, right_border, 1):
        if list_float_numbers[i] > list_float_numbers[i + 1]:
          list_float_numbers[i], list_float_numbers[i + 1] = list_float_numbers[i + 1], list_float_numbers[i]
    right_border -= 1

    for i in range(right_border, left_border, -1):
        if list_float_numbers[i - 1] > list_float_numbers[i]:
            list_float_numbers[i], list_float_numbers[i - 1] = list_float_numbers[i - 1], list_float_numbers[i]
    left_border += 1                

print('Shaker sort:',list_float_numbers) 



#3 Selection sort

# Генерация случайных точек внутри окружности радиуса r
count = 42000
birth_day=25
birth_month=2
R=birth_day/birth_month
r=[random.uniform(0, R) for r in range(count)]
phi=[random.uniform(0,2*mt.pi) for phi in range(count)]
a=[]
compl = [random.uniform(0, R)]
for i in range(count):
    a.append(r[i]*mt.cos(phi[i]))
b=[]
for i in range(count):
    b.append(r[i]*mt.sin(phi[i]))
c=[]
for i in range(count):
    c.append(complex(a[i],b[i]))
modul=[]
for i in range(count):
    modul.append(mt.sqrt((a[i])**2+(b[i])**2))

def selectionSort(modul):
    for i in range(len(modul)-1):
        min_idx = i
        for idx in range(i + 1, len(modul)-1):
            if modul[idx] < modul[min_idx]:
                min_idx = idx
        modul[i], modul[min_idx] = modul[min_idx], modul[i]
    return modul
print('Selection sort:',selectionSort(modul))


