def bubble_sort(arr):
    n = len(arr)
    if n <= 1:
        return arr
    swapped = True
    while swapped:
        swapped = False
        for i in range(1, n):
            if len(arr[i - 1]) > len(arr[i]):
                arr[i - 1], arr[i] = arr[i], arr[i - 1]
                swapped = True
        n -= 1
    return arr

