# List of complex numbers
def get_list_3():
    def get_point():
        r = 26 / 3
        while True:
            x = random.random() * r - r
            y = random.random() * r - r
            if x ** 2 + y ** 2 < r:
                point = complex(x, y) 
                return (point.real ** 2 + point.imag ** 2) ** 0.5

    return [get_point() for _ in range(42000)]


list_3 = get_list_3()


