def counting_sort(list):
    min_val = min(list)
    max_val = max(list)
    count_arr = [0] * (max_val - min_val + 1)
    for num in list:
        count_arr[num - min_val] += 1
    sorted_arr = []
    for i in range(len(count_arr)):
        sorted_arr.extend([i + min_val] * count_arr[i])
    return sorted_arr
