def shaker_sort(list):
    i = 0
    while i != len(list):
        if list[i] < list[i-1]:
            temp1 = list[i]
            list[i] = list[i - 1]
            list[i - 1] = temp1
        i +=1
        j = len(list) - 1
        while j >= i:
            if list[j] < list[j-1]:
                temp2 = list[j]
                list[j] = list[j - 1]
                list[j - 1] = temp2
            j -= 1
    return list
