def bubble_sort_complex(arr):
    n = len(arr)
    for i in range(n - 1):
        for j in range(0, n - i - 1):
            if abs(arr[j]) > abs(arr[j + 1]):
                arr[j], arr[j + 1] = arr[j + 1], arr[j]




