    # гномья сортировка 4текст
def gnom_sort(list, func):
    i = 1
    while i != len(list):
        if i == 0 or func(list[i]) >= func(list[i-1]):
            i += 1
        else:
            temp = list[i]
            list[i] = list[i-1]
            list[i-1] = temp
            i -= 1
    return list
