# insertion sort, сортировка вставкой
def insertion_sort(massiv):
    for i in range(1,len(massiv)):
        key=massiv[i]
        j=i-1
        while (j>=0 and key<massiv[j]):
            massiv[j+1]=massiv[j]
            j-=1
        massiv[j+1]=key
    return massiv
sort_chisla=insertion_sort(chisla)
print("Сортировка вставкой",sort_chisla)

# Quicksort, быстрая сортировка
def sort(massiv,l,h):
    key=massiv[h]
    i=l-1
    for j in range(l,h):
        if massiv[j]<key:
            i+=1
            massiv[i],massiv[j]=massiv[j],massiv[i]
    massiv[i+1],massiv[h]=massiv[h],massiv[i+1]
    return i+1
def quick_sort(massiv,l,h):
    if l<h:
        sr=sort(massiv,l,h)
        quick_sort(massiv,l,sr-1)
        quick_sort(massiv,sr+1,h)
quick_sort(random1,0,len(random1)-1)
print("Быстрая сортировка:",random1)

# Gnome sort, гномья сортировка
def gnome_sort(massiv):
    i=1
    while i<len(massiv):
        if i==0 or massiv[i-1]<=massiv[i]:
            i+=1
        else:
            massiv[i],massiv[i-1]=massiv[i-1],massiv[i]
            i-=1
    return massiv

