print("Гномья сортировка:",sort_random2)

# shaker sort, сортировка перемешиванием
def shaker_sort(massiv):
    left=0
    right=len(massiv)-1
    swap=True
    while swap:
        swap=False
        for i in range(left,right):
            if massiv[i]>massiv[i+1]:
                massiv[i],massiv[i+1]=massiv[i+1],massiv[i]
                swap=True
        right-=1
        if not swap:
            break
        for i in range(right,left,-1):
            if massiv[i-1]>massiv[i]:
                massiv[i],massiv[i-1]=massiv[i-1],massiv[i]
                swap=True
        left+=1
    return massiv
sort_slova=shaker_sort(slova)
print("Сортировка пермешиванием:",sort_slova)


